import React from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { useDispatch } from "react-redux";
import { login } from "../actions/userActions";
import "../styles.css";
import pic from "../images/wp2.jpg";

export const Login = () => {
  const dispatch = useDispatch();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const user = {
        username,
        password,
      };

      dispatch(login(username, password)); // Dispatching login action

      // This block of code will be removed once login action handles redirection
      localStorage.clear();
      localStorage.setItem("username", username);
      //Redirect to dashboard after login.
      window.location.href = "/dashboard";
    } catch (error) {
      console.error(error);
      setError("Invalid Credentials!");
    }
  };

  return (
    <div
      style={{
        position: "relative",
        height: "100vh",
        width: "100vw",
        overflow: "hidden",
      }}
    >
      <div
        style={{
          content: "",
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          opacity: "24%",
          backgroundImage: `url(${pic})`,
          backgroundPosition: "center",
          backgroundSize: "cover",
          backgroundRepeat: "no-repeat",
          zIndex: -1,
        }}
      />

      <h1
        style={{
          color: "#004cff",
          fontSize: "150px",
          position: "absolute",
          right: "124px",
          margin: 0,
          padding: 0,
        }}
      >
        인뱅
      </h1>
      <h2
        style={{
          color: "#004cff",
          position: "absolute",
          fontSize: "50px",
          top: "110px",
          right: "24px",
          transform: "rotate(-24deg)",
        }}
      >
        In-Bang
      </h2>

      <div
        style={{
          margin: "auto",
          top: "150px",
          position: "relative",
          width: "300px",
          border: "2px solid rgba(18, 0, 184, 1)",
          backgroundColor: "rgba(18, 0, 184, 1)",
          borderRadius: "24px",
          color: "#ffffff",
          padding: "20px",
        }}
      >
        <Link
          to="/"
          style={{
            position: "absolute",
            right: "10px",
            top: "10px",
            textDecoration: "none",
          }}
        >
          <button
            style={{
              cursor: "pointer",
              border: "2px solid rgba(252, 32, 3, 1)",
              backgroundColor: "rgba(252, 32, 3, 1)",
              borderRadius: "24px",
              color: "#ffffff",
              color: "#000000",
              padding: "5px",
            }}
          >
            X
          </button>
        </Link>
        {/* Removed local state management */}
        <form onSubmit={handleSubmit}>
          {/* Form inputs and submit button */}
        </form>
      </div>
    </div>
  );
};

export default Login;
