import React from "react";
import "../styles.css";
import { Link } from "react-router-dom";
import pic from "../images/wp2.jpg";

function Login() {
  return (
    <div
      style={{
        position: "relative",
        height: "100vh",
        width: "100vw",
        overflow: "hidden",
      }}
    >
      <div
        style={{
          content: "",
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          opacity: "66%",
          backgroundImage: `url(${pic})`,
          backgroundPosition: "center",
          backgroundSize: "cover",
          backgroundRepeat: "no-repeat",
          zIndex: -1,
        }}
      />

      <h1
        style={{
          color: "#004cff",
          fontSize: "150px",
          position: "absolute",
          right: "124px",
          margin: 0,
          padding: 0,
        }}
      >
        인뱅
      </h1>
      <h2
        style={{
          color: "#004cff",
          position: "absolute",
          fontSize: "50px",
          top: "110px",
          right: "24px",
          transform: "rotate(-24deg)",
        }}
      >
        In-Bang
      </h2>

      <div
        style={{
          backgroundColor: "#1200b8",
          padding: "20px",
          color: "white",
          margin: "auto",
          top: "150px",
          position: "relative",
          width: "300px",
        }}
      >
        <Link
          to="/"
          style={{
            position: "absolute",
            right: "10px",
            top: "10px",
            textDecoration: "none",
          }}
        >
          <button
            style={{
              backgroundColor: "#fc2003",
              color: "#000000",
              padding: "5px",
            }}
          >
            X
          </button>
        </Link>
        <p
          style={{
            color: "#000000",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          Welcome!
        </p>
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            marginBottom: "10px",
          }}
        >
          <label>Username:</label>
          <input type="text" name="username" />
        </div>
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            marginBottom: "10px",
          }}
        >
          <label>Password:</label>
          <input type="password" name="password" />
        </div>
        <br />
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            marginTop: "20px",
          }}
        >
          <Link to="/dashboard">
            <button
              style={{
                backgroundColor: "#000000",
                color: "#ffffff",
                padding: "5px",
              }}
            >
              Login
            </button>
          </Link>
        </div>
        <p
          style={{
            color: "#000000",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            whiteSpace: "nowrap",
          }}
        >
          Don't have an account? Click below.
        </p>
        <Link
          to="/registration"
          style={{
            color: "#ffffff",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          Register
        </Link>
      </div>
    </div>
  );
}

export default Login;
