import React, { useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import Footer from "../components/Footer.js";
import "./InvisiScroll.css";
import pic from "../images/homepage.jpg";

function Contact() {
  useEffect(() => {
    return () => {};
  }, []);

  const navigate = useNavigate();

  const handleGoBack = () => {
    // Handle go back functionality
  };

  useEffect(() => {
    let timeoutId;

    const handleUserActivity = () => {
      if (!showBackButton) {
        setShowBackButton(true);
      }

      clearTimeout(timeoutId);
      timeoutId = setTimeout(() => {
        setShowBackButton(false);
      }, 3000); // Hide back button after 3 seconds of inactivity
    };

    document.addEventListener("mousemove", handleUserActivity);
    document.addEventListener("keypress", handleUserActivity);

    return () => {
      document.removeEventListener("mousemove", handleUserActivity);
      document.removeEventListener("keypress", handleUserActivity);
    };
  }, [showBackButton]);

  return (
    <div className="card-container">
      <Footer />
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          minHeight: "90vh",
          padding: "2vh",
        }}
      >
        <div
          style={{
            border: "2vh solid #ccc",
            padding: "2vh",
            textAlign: "center",
          }}
        >
          {/* 서비스 이용 약관 텍스트 */}
          <h1>Contact Sales</h1>
          <p>
            If you want to send us a business mail, send us an e-mail:
            sales@inbang.com
          </p>
          <h1>Report Error</h1>
          <p>
            If you want to report an error, or if you want to make a suggestion,
            send us an e-mail: help@inbang.com
          </p>

          {/* 약관의 나머지 내용 */}
        </div>

        {/* Back Button */}
        <button
          onClick={handleGoBack}
          style={{
            position: "fixed",
            top: "2vh",
            right: "2vw",
            cursor: "pointer",
            border: "0.4vh solid rgba(18, 0, 184, 1)",
            backgroundColor: "rgba(18, 0, 134, 1)",
            borderRadius: "1vh",
            color: "#ffffff",
            padding: "0.01vh 1vw",
            fontSize: "4.5vh",
            fontWeight: "bolder",
            transform: "scaleY(-1)",
            zIndex: 3,
          }}
        >
          ↩
        </button>
      </div>
    </div>
  );
}

export default Contact;
