import React, { useEffect, useState } from "react";
import Header from "../components/Header";
import { useNavigate } from "react-router-dom";
import { logout } from "../actions/userActions";
import { useDispatch, useSelector } from "react-redux";
import FooterTransparent from "../components/FooterTransparent";
import sampleVideo from "../videos/sampleVideo.mp4";
import "./mediaqueries.css";

const Dashboard = () => {
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [searchQuery, setSearchQuery] = useState(""); // State for search query
  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [showMessage, setShowMessage] = useState(false); // State to control message visibility

  // Movie data
  const movieData = [
    {
      title: "Talong 1",
      genre: "Action",
      rating: 8.5,
    },
    {
      title: "Talong 2",
      genre: "Comedy",
      rating: 7.8,
    },
    // Add more movie objects here
  ];

  // Filtered movie data based on search query
  const filteredMovies = movieData.filter((movie) =>
    movie.title.toLowerCase().includes(searchQuery.toLowerCase())
  );

  useEffect(() => {
    // Show message for a short duration before redirecting
    if (!userInfo) {
      setShowMessage(true); // Show the message
      const timer = setTimeout(() => {
        navigate("/login"); // Redirect to login after a delay
      }, 2000); // Delay in milliseconds (e.g., 2000 milliseconds = 2 seconds)
      return () => clearTimeout(timer); // Clear the timer if the component unmounts
    }
  }, [userInfo, navigate]);

  const logoutHandler = () => {
    dispatch(logout());
    localStorage.removeItem("user");
    navigate("/login"); // Redirect to login after logout
  };

  const errormsg = {
    color: "#ff0000",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "100vh", // Set height to full viewport height
    fontSize: "15vw",
    fontWeight: "bolder",
  };

  const handlePosterClick = (index) => {
    setSelectedMovie(filteredMovies[index]);
  };

  const handleCloseModal = () => {
    setSelectedMovie(null);
  };

  // If user is not logged in, only show the "Login first!" message
  if (!userInfo) {
    return <div style={errormsg}>Login first!</div>;
  }

  return (
    <div className="container">
      <FooterTransparent />
      <div>
        <Header user={userInfo} logoutHandler={logoutHandler} />
        <input
          type="text"
          placeholder="Search for a movie..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        <div
          style={{
            display: "flex",
            flexWrap: "nowrap", //Makes a ridiculous amount of margin somehow so I set it to nowrap.
            flexDirection: "row",
            alignItems: "center",
            justifyContent: "center",
            textAlign: "center",
            height: "25vh",
          }}
        >
          {filteredMovies.map((movie, index) => (
            <div
              key={index}
              onClick={() => handlePosterClick(index)}
              style={{
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                justifyContent: "center",
                textAlign: "center",
                marginLeft: "3vh",
                marginRight: "3vh",
                marginTop: "44vh",
                marginBottom: "-31vh",
                backgroundColor: "#f9f9f9",
                borderRadius: "1vh",
                boxShadow: "0 0 2.5vw rgba(0, 0, 0, 0.1)",
                overflow: "hidden",
                cursor: "pointer",
              }}
            >
              {selectedMovie === movie && (
                <video
                  controls
                  src={sampleVideo}
                  style={{
                    width: "100%",
                    height: "auto",
                    borderBottom: "0.25vh solid #ddd",
                  }}
                />
              )}
              {selectedMovie !== movie && (
                <img
                  src={require("../images/sample.png")}
                  alt={`Poster ${index + 1}`}
                  style={{
                    width: "100%",
                    height: "auto",
                    borderBottom: "0.25vh solid #ddd",
                  }}
                />
              )}
              {selectedMovie !== movie && (
                <div style={{ padding: "1.5vh", width: "100%" }}>
                  <h2
                    style={{ margin: "0", fontSize: "3vh", color: "#000000" }}
                  >
                    {movie.title}
                  </h2>
                  <p
                    style={{
                      margin: "1vh 0",
                      fontSize: "2.5vh",
                      color: "#000000",
                    }}
                  >
                    Genre: {movie.genre}
                  </p>
                  <p
                    style={{
                      margin: "1vh 0",
                      fontSize: "2.5vh",
                      color: "#000000",
                    }}
                  >
                    Rating: {movie.rating}
                  </p>
                </div>
              )}
            </div>
          ))}
          {selectedMovie && (
            <div
              style={{
                position: "fixed",
                top: 0,
                left: 0,
                width: "100%",
                height: "100%",
                backgroundColor: "rgba(0, 0, 0, 0.5)",
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                zIndex: "999",
              }}
              onClick={handleCloseModal}
            >
              <video
                controls
                autoPlay
                src={sampleVideo}
                style={{
                  maxWidth: "80vw",
                  maxHeight: "80vh",
                }}
              />
            </div>
          )}
        </div>
        <br />
      </div>
    </div>
  );
};

export default Dashboard;
