import React, { useEffect, useState } from "react";
import Header from "../components/Header";
import { useNavigate } from "react-router-dom";
import { logout } from "../actions/userActions";
import { useDispatch, useSelector } from "react-redux";

const Dashboard = () => {
  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [showMessage, setShowMessage] = useState(false); // State to control message visibility

  useEffect(() => {
    // Show message for a short duration before redirecting
    if (!userInfo) {
      setShowMessage(true); // Show the message
      const timer = setTimeout(() => {
        navigate("/login"); // Redirect to login after a delay
      }, 2000); // Delay in milliseconds (e.g., 2000 milliseconds = 2 seconds)
      return () => clearTimeout(timer); // Clear the timer if the component unmounts
    }
  }, [userInfo, navigate]);

  const logoutHandler = () => {
    dispatch(logout());
    localStorage.removeItem("user");
    navigate("/login"); // Redirect to login after logout
  };

  const errormsg = {
    color: "#ff0000",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "100vh", // Set height to full viewport height
    fontSize: "100vh",
    fontWeight: "bolder",
  };

  const msg = {
    color: "#ffffff",
  };

  if (!userInfo) {
    return <div style={errormsg}>Login first!</div>;
  }

  return (
    <div>
      <Header user={userInfo} logoutHandler={logoutHandler} />
      <div style={msg}>Dashboard Hello</div>
    </div>
  );
};

export default Dashboard;
