import React, { useEffect, useState } from "react";
import Header from "../components/Header";
import { useNavigate } from "react-router-dom";
import { logout } from "../actions/userActions";
import { useDispatch, useSelector } from "react-redux";
import "./InvisiScroll.css";
import FooterDash from "../components/FooterDash";
import vid1 from "../videos/movies/ABirdsNest.mp4";
import vid2 from "../videos/movies/Boruto.mp4";
import vid3 from "../videos/movies/Extraordinary.mp4";
import vid4 from "../videos/movies/HarryPotterandtheChamberofSecrets.mp4";
import vid5 from "../videos/movies/Hunchback.mp4";
import vid6 from "../videos/movies/Joker.mp4";
import vid7 from "../videos/movies/MobileSuitGundam.mp4";
import vid8 from "../videos/movies/Ponyo.mp4";
import vid9 from "../videos/movies/RockyV.mp4";
import vid10 from "../videos/movies/StarWarsEpisodeVIIITheLastJedi.mp4";
import vid11 from "../videos/movies/TheBadGuys.mp4";
import vid12 from "../videos/movies/THELEGO®MOVIE™4DANEWADVENTURE.mp4";
import vid13 from "../videos/movies/WeBareBears.mp4";
import "./mediaqueries.css";
import SearchBar from "../components/SearchBar";

const Dashboard = () => {
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const movieData = [
    {
      title: "A Bird's Nest",
      genre: "Horror",
      rating: "6.1/10",
      src: vid1,
      poster: require("../videos/movies/ABirdsNest.jpg"),
    },
    {
      title: "Boruto: Naruto the Movie",
      genre: "Action",
      rating: "7.8/10",
      src: vid2,
      poster: require("../videos/movies/Boruto.jpg"),
    },
    {
      title: "Extraordinary",
      genre: "Drama",
      rating: "7.4/10",
      src: vid3,
      poster: require("../videos/movies/Extraordinary.jpg"),
    },
    {
      title: "Harry Potter and the Chamber of Secrets",
      genre: "Fantasy",
      rating: "7.4/10",
      src: vid4,
      poster: require("../videos/movies/HarryPotterandtheChamberofSecrets.jpg"),
    },
    {
      title: "Hunchback",
      genre: "Animation",
      rating: "6.9/10",
      src: vid5,
      poster: require("../videos/movies/Hunchback.jpg"),
    },
    {
      title: "Joker",
      genre: "Crime",
      rating: "8.4/10",
      src: vid6,
      poster: require("../videos/movies/Joker.jpg"),
    },
    {
      title: "Mobile Suit Gundam",
      genre: "Sci-Fi",
      rating: "7.9/10",
      src: vid7,
      poster: require("../videos/movies/MobileSuitGundam.jpg"),
    },
    {
      title: "Ponyo",
      genre: "Family",
      rating: "7.7/10",
      src: vid8,
      poster: require("../videos/movies/Ponyo.jpg"),
    },
    {
      title: "Rocky V",
      genre: "Sport",
      rating: "5.3/10",
      src: vid9,
      poster: require("../videos/movies/RockyV.jpg"),
    },
    {
      title: "Star Wars Episode VIII: The Last Jedi",
      genre: "Action",
      rating: "7/10",
      src: vid10,
      poster: require("../videos/movies/StarWarsEpisodeVIIITheLastJedi.jpg"),
    },
    {
      title: "The Bad Guys",
      genre: "Animation",
      rating: "6.7/10",
      src: vid11,
      poster: require("../videos/movies/TheBadGuys.jpg"),
    },
    {
      title: "THE LEGO® MOVIE™ 4D A NEW ADVENTURE",
      genre: "Animation",
      rating: "6.2/10",
      src: vid12,
      poster: require("../videos/movies/THELEGO®MOVIE™4DANEWADVENTURE.jpg"),
    },
    {
      title: "We Bare Bears",
      genre: "Animation",
      rating: "7.9/10",
      src: vid13,
      poster: require("../videos/movies/WeBareBears.jpg"),
    },
  ];

  const handlePosterClick = (index) => {
    setSelectedMovie(movieData[index]);
  };

  const handleCloseModal = () => {
    setSelectedMovie(null);
  };

  const SearchFunction = (query) => {
    setSearchQuery(query);
  };

  useEffect(() => {
    // Redirect if user is not logged in
    if (!userInfo) {
      setShowMessage(true);
      const timer = setTimeout(() => {
        navigate("/login");
      }, 2000);
      return () => clearTimeout(timer);
    }
  }, [userInfo, navigate]);

  const logoutHandler = () => {
    dispatch(logout());
    localStorage.removeItem("user");
    navigate("/login");
  };

  const filteredMovies = movieData.filter((movie) => {
    const titleMatch = movie.title
      .toLowerCase()
      .includes(searchQuery.toLowerCase());
    const genreMatch = movie.genre
      .toLowerCase()
      .includes(searchQuery.toLowerCase());
    return titleMatch || genreMatch;
  });

  const errormsg = {
    color: "#ff0000",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "100vh",
    fontSize: "15vw",
    fontWeight: "bolder",
  };

  return (
    <div className="card-container">
      <SearchBar onSearch={SearchFunction} />
      <FooterDash />
      <div>
        <Header user={userInfo} logoutHandler={logoutHandler} />
        <div
          style={{
            display: "flex",
            flexWrap: "wrap",
            justifyContent: "center",
          }}
        >
          {filteredMovies.map((movie, index) => (
            <div
              key={index}
              onClick={() => handlePosterClick(index)}
              style={{
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                justifyContent: "center",
                margin: "2vh",
                width: "30vh", // Adjust the width to control the size of the card
              }}
            >
              <img
                src={movie.poster}
                alt={`Poster ${index + 1}`}
                style={{
                  width: "100%",
                  height: "auto",
                  borderRadius: "10px", // Add border-radius for rounded corners
                  boxShadow: "0 0 10px rgba(0, 0, 0, 0.1)", // Add boxShadow for shadow effect
                  cursor: "pointer",
                }}
              />
              {selectedMovie === movie && (
                <video
                  controls
                  src={movie.src}
                  style={{
                    width: "100%",
                    height: "auto",
                    borderRadius: "10px", // Add border-radius for rounded corners
                    boxShadow: "0 0 10px rgba(0, 0, 0, 0.1)", // Add boxShadow for shadow effect
                    cursor: "pointer",
                  }}
                />
              )}
              <div style={{ padding: "10px", width: "100%" }}>
                <h2 style={{ margin: "0", fontSize: "18px", color: "#000000" }}>
                  {movie.title}
                </h2>
                <p
                  style={{
                    margin: "5px 0",
                    fontSize: "14px",
                    color: "#000000",
                  }}
                >
                  Genre: {movie.genre}
                </p>
                <p
                  style={{
                    margin: "0",
                    fontSize: "14px",
                    color: "#000000",
                  }}
                >
                  Rating: {movie.rating}
                </p>
              </div>
            </div>
          ))}
          {selectedMovie && (
            <div
              style={{
                position: "fixed",
                top: 0,
                left: 0,
                width: "100%",
                height: "100%",
                backgroundColor: "rgba(0, 0, 0, 0.5)",
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                zIndex: "999",
              }}
              onClick={handleCloseModal}
            >
              <video
                controls
                autoPlay
                src={selectedMovie.src}
                style={{
                  maxWidth: "80vw",
                  maxHeight: "80vh",
                }}
              />
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
