import React, { useState } from "react";
import "../styles.css";
import { Link } from "react-router-dom";

function Subscription() {
  const [selectedPlan, setSelectedPlan] = useState(null);

  return (
    <div onClick={() => setSelectedPlan(null)}>
      <h1 style={{ color: "#004cff", fontSize: "100px" }}>인뱅</h1>
      <h2 style={{ color: "#004cff" }}>In-Bang</h2>
      <p style={{ color: "#ffffff" }}>
        <br />
        Choose your stated plan
      </p>
      <p style={{ color: "#ffffff", fontSize: "12px" }}>
        <br />
        /Ad free.
        <br />
        /Change or cancel plans anytime.
      </p>
      <div style={{ position: "relative" }}>
        <table
          style={{
            backgroundColor: "#1200b8",
            color: "#ffffff",
            marginTop: "20px",
            marginLeft: "auto",
            marginRight: "auto",
          }}
        >
          <tbody>
            <tr>
              <td style={{ padding: "10px" }}></td>
              <td style={{ padding: "10px" }}>
                <button
                  style={{
                    backgroundColor:
                      selectedPlan === "Standard" ? "#fc2003" : "transparent",
                    color: "#ffffff",
                    border: "none",
                  }}
                  onClick={(e) => {
                    e.stopPropagation();
                    setSelectedPlan("Standard");
                  }}
                >
                  Standard
                </button>
              </td>
              <td style={{ padding: "10px" }}>
                <button
                  style={{
                    backgroundColor:
                      selectedPlan === "Premium" ? "#fc2003" : "transparent",
                    color: "#ffffff",
                    border: "none",
                  }}
                  onClick={(e) => {
                    e.stopPropagation();
                    setSelectedPlan("Premium");
                  }}
                >
                  Premium
                </button>
              </td>
            </tr>
            {/* ... rest of the table content ... */}
          </tbody>
        </table>
        <Link
          to="/"
          style={{
            position: "absolute",
            right: "10px",
            top: "10px",
            textDecoration: "none",
          }}
        >
          <button
            style={{
              backgroundColor: "#fc2003",
              color: "#000000",
              padding: "5px",
            }}
          >
            X
          </button>
        </Link>
      </div>
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          marginTop: "20px",
        }}
      >
        <Link to="/login">
          <button
            style={{
              backgroundColor: "#1200b8",
              color: "#ffffff",
              padding: "5px",
            }}
          >
            Next
          </button>
        </Link>
      </div>
    </div>
  );
}

export default Subscription;
