import React, { useEffect, useState } from "react";
import Header from "../components/Header";
import { useNavigate } from "react-router-dom";
import { logout } from "../actions/userActions";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import sampleVideo from "../videos/sampleVideo.mp4";

const Movie = () => {
  const [selectedMovie, setSelectedMovie] = useState(null);
  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [showMessage, setShowMessage] = useState(false); // State to control message visibility

  useEffect(() => {
    // Show message for a short duration before redirecting
    if (!userInfo) {
      setShowMessage(true); // Show the message
      const timer = setTimeout(() => {
        navigate("/login"); // Redirect to login after a delay
      }, 2000); // Delay in milliseconds (e.g., 2000 milliseconds = 2 seconds)
      return () => clearTimeout(timer); // Clear the timer if the component unmounts
    }
  }, [userInfo, navigate]);

  const logoutHandler = () => {
    dispatch(logout());
    localStorage.removeItem("user");
    navigate("/login"); // Redirect to login after logout
  };

  const errormsg = {
    color: "#ff0000",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "100vh", // Set height to full viewport height
    fontSize: "100px",
    fontWeight: "bolder",
  };

  const msg = {
    color: "#ffffff",
  };

  if (!userInfo) {
    return <div style={errormsg}>Login first!</div>;
  }

  const movieData = [
    {
      title: "Talong 1",
      genre: "Action",
      rating: 8.5,
    },
    {
      title: "Talong 2",
      genre: "Comedy",
      rating: 7.8,
    },
    {
      title: "Talong 3",
      genre: "Drama",
      rating: 9.2,
    },
    {
      title: "Talong 4",
      genre: "Adventure",
      rating: 8.0,
    },
    {
      title: "Talong 5",
      genre: "Sci-Fi",
      rating: 8.7,
    },
    {
      title: "Talong 6",
      genre: "Thriller",
      rating: 7.9,
    },
  ];

  const handlePosterClick = (index) => {
    setSelectedMovie(movieData[index]);
  };

  const handleCloseModal = () => {
    setSelectedMovie(null);
  };

  return (
    <>
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100%",
          backgroundColor: "#333",
          padding: "10px",
          zIndex: "999",
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
        }}
      >
        <Link
          to="/dashboard"
          style={{
            textDecoration: "none",
            color: "#ffffff",
            fontSize: "16px",
            fontWeight: "bold",
          }}
        >
          Return to Dashboard
        </Link>
        <Link
          to="/user-settings"
          style={{
            textDecoration: "none",
            color: "#ffffff",
            fontSize: "16px",
            fontWeight: "bold",
          }}
        >
          User Profile Settings
        </Link>
      </div>
      <div style={{ paddingTop: "50px" }}>
        <div
          style={{
            display: "flex",
            flexWrap: "wrap",
            justifyContent: "space-evenly",
            padding: "20px",
          }}
        >
          {movieData.map((movie, index) => (
            <div
              key={index}
              onClick={() => handlePosterClick(index)}
              style={{
                width: "200px",
                margin: "20px",
                marginBottom: "100px",
                backgroundColor: "#f9f9f9",
                borderRadius: "5px",
                boxShadow: "0 0 10px rgba(0, 0, 0, 0.1)",
                overflow: "hidden",
                cursor: "pointer",
              }}
            >
              {selectedMovie === movie && (
                <video
                  controls
                  src={sampleVideo}
                  style={{
                    width: "100%",
                    height: "auto",
                    borderBottom: "1px solid #ddd",
                  }}
                />
              )}
              {selectedMovie !== movie && (
                <img
                  src={require("../images/sample.png")}
                  alt={`Poster ${index + 1}`}
                  style={{
                    width: "100%",
                    height: "auto",
                    borderBottom: "1px solid #ddd",
                  }}
                />
              )}
              <div style={{ padding: "10px" }}>
                <h2 style={{ margin: "0", fontSize: "18px" }}>{movie.title}</h2>
                <p style={{ margin: "5px 0", fontSize: "14px" }}>
                  Genre: {movie.genre}
                </p>
                <p style={{ margin: "5px 0", fontSize: "14px" }}>
                  Rating: {movie.rating}
                </p>
              </div>
            </div>
          ))}
        </div>
        {selectedMovie && (
          <div
            style={{
              position: "fixed",
              top: "0",
              left: "0",
              width: "100%",
              height: "100%",
              backgroundColor: "rgba(0, 0, 0, 0.5)",
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
              zIndex: "999",
            }}
            onClick={handleCloseModal}
          >
            <video
              controls
              autoPlay
              src={sampleVideo}
              style={{
                maxWidth: "80%",
                maxHeight: "80%",
              }}
            />
          </div>
        )}
      </div>
    </>
  );
};

export default Movie;
