import React, { useEffect } from "react";
import { Link, NavLink } from "react-router-dom";
import pic from "../images/homepage.jpg";
import pic2 from "../images/wp3.jpg";

function Homepage() {
  useEffect(() => {
    return () => {};
  }, []);

  return (
    <div
      style={{
        position: "relative",
        width: "100%",
        height: "100vh",
        overflow: "hidden",
      }}
    >
      <div
        style={{
          content: "",
          position: "absolute",
          width: "100%",
          height: "100%",
          backgroundImage: `url(${pic})`,
          backgroundPosition: "center",
          backgroundSize: "cover",
          backgroundRepeat: "no-repeat",
          zIndex: -1,
        }}
      />
      <div style={{ margin: "24px", position: "relative" }}>
        <div
          style={{
            position: "absolute",
            top: "24px",
            right: "24px",
            zIndex: 2,
          }}
        >
          <NavLink to="/login">
            <button
              style={{
                cursor: "pointer",
                border: "2px solid rgba(18, 0, 184, 1)",
                backgroundColor: "rgba(18, 0, 184, 1)",
                borderRadius: "5px",
                color: "#ffffff",
                padding: "10px",
                fontSize: "18px",
              }}
            >
              Login
            </button>
          </NavLink>
        </div>
        <div
          style={{
            position: "fixed",
            bottom: 0,
            width: "100%",
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
            textAlign: "center",
            padding: "2%",
            boxSizing: "border-box",
          }}
        >
          <p
            style={{
              color: "#ffffff",
              width: "50%",
              border: "2px solid rgba(255, 0, 38, 0.5)",
              backgroundColor: "rgba(255, 0, 38, 0.5)",
              borderRadius: "5px",
              padding: "1%",
              textAlign: "center",
              fontSize: "calc(1vw + 0.5vh + 14px)",
            }}
          >
            Are you ready to watch exciting movies? Click the box below to get
            you started!
          </p>
          <Link to="/login">
            <button
              style={{
                cursor: "pointer",
                border: "2px solid rgba(18, 0, 184, 1)",
                backgroundColor: "rgba(18, 0, 184, 1)",
                borderRadius: "5px",
                color: "#ffffff",
                padding: "1%",
                fontSize: "calc(1vw + 0.5vh + 12px)",
                marginTop: "1%",
              }}
            >
              Get Started â†’
            </button>
          </Link>
        </div>
      </div>
    </div>
  );
}

export default Homepage;
