import React, { useEffect, useState } from "react";
import Header from "../components/Header";
import { useNavigate } from "react-router-dom";
import { logout } from "../actions/userActions";
import { useDispatch, useSelector } from "react-redux";
import "./InvisiScroll.css";
import FooterDash from "../components/FooterDash";
import SearchBar from "../components/SearchBar";

// Import movie videos and posters
import vid1 from "../videos/movies/ABirdsNest.mp4";
import vid2 from "../videos/movies/Boruto.mp4";
import vid3 from "../videos/movies/Extraordinary.mp4";
// Import other videos...

const Genre = () => {
  const [selectedMovie, setSelectedMovie] = useState(null);
  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [showMessage, setShowMessage] = useState(false); // State to control message visibility
  const [searchQuery, setSearchQuery] = useState(""); // State for search query

  // Movie data grouped by genre
  const movieData = {
    Action: [
      {
        title: "Boruto: Naruto the Movie",
        rating: "7.8/10",
        src: vid2,
        poster: require("../videos/movies/Boruto.jpg"),
      },
      // Add other action movies...
    ],
    Drama: [
      {
        title: "Extraordinary",
        rating: "7.4/10",
        src: vid3,
        poster: require("../videos/movies/Extraordinary.jpg"),
      },
      // Add other drama movies...
    ],
    Horror: [
      {
        title: "A Bird's Nest",
        rating: "6.1/10",
        src: vid1,
        poster: require("../videos/movies/ABirdsNest.jpg"),
      },
      // Add other horror movies...
    ],
  };

  const SearchFunction = (query) => {
    setSearchQuery(query);
  };

  useEffect(() => {
    // Show message for a short duration before redirecting
    if (!userInfo) {
      setShowMessage(true);
      const timer = setTimeout(() => {
        navigate("/login");
      }, 2000);
      return () => clearTimeout(timer);
    }
  }, [userInfo, navigate, searchQuery]);

  const logoutHandler = () => {
    dispatch(logout());
    localStorage.removeItem("user");
    navigate("/login");
  };

  const errormsg = {
    color: "#ff0000",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "100vh",
    fontSize: "15vw",
    fontWeight: "bolder",
  };

  const handlePosterClick = (movie) => {
    setSelectedMovie(movie);
  };

  const handleCloseModal = () => {
    setSelectedMovie(null);
  };

  if (!userInfo) {
    return <div style={errormsg}>Login first!</div>;
  }

  return (
    <div className="card-container">
      <SearchBar onSearch={SearchFunction} />
      <FooterDash />
      <p
        style={{
          color: "transparent ",
          backgroundColor: "transparent",
        }}
      >
        <br />
        <br />
        <br />
        <br />
        <br />
        <br />
        <br />
        <br />
        <br />
      </p>
      <div>
        <Header user={userInfo} logoutHandler={logoutHandler} />
        {/* Render tables for each genre */}
        {Object.entries(movieData).map(([genre, movies]) => (
          <div key={genre}>
            <h2>{genre}</h2>
            <div
              style={{
                display: "flex",
                flexWrap: "wrap",
                justifyContent: "center",
              }}
            >
              {movies
                .filter((movie) =>
                  movie.title.toLowerCase().includes(searchQuery.toLowerCase())
                )
                .map((movie, index) => (
                  <div
                    key={index}
                    onClick={() => handlePosterClick(movie)}
                    style={{
                      display: "flex",
                      flexDirection: "column",
                      alignItems: "center",
                      justifyContent: "center",
                      textAlign: "center",
                      marginLeft: "2vh",
                      marginRight: "2vh",
                      marginBottom: "5vh", // Adjusted margin to create space between movie boxes
                      backgroundColor: "#f9f9f9",
                      width: "17.5%",
                      height: "51vh", // Fixed height for each movie box
                      borderRadius: "1vh",
                      boxShadow: "0 0 2.5vw rgba(0, 0, 0, 0.1)",
                      overflow: "hidden", // Adjusted overflow to hide extra content
                      cursor: "pointer",
                      border: "0.3vh solid white",
                      borderRadius: "2vh",
                    }}
                  >
                    <img
                      src={movie.poster}
                      alt={`Poster ${index + 1}`}
                      style={{
                        width: "100%",
                        height: "auto",
                      }}
                    />
                    {selectedMovie === movie && (
                      <video
                        controls
                        src={movie.src}
                        style={{
                          width: "100%",
                          height: "auto",
                        }}
                      />
                    )}
                    <div style={{ padding: "2vh", width: "100%" }}>
                      <h2
                        style={{
                          margin: "0",
                          fontSize: "3vh",
                          color: "#000000",
                        }}
                      >
                        {movie.title}
                      </h2>
                      <p
                        style={{
                          margin: "2vh 0",
                          fontSize: "2.5vh",
                          color: "#000000",
                        }}
                      >
                        Genre: {genre}
                      </p>
                      <p
                        style={{
                          margin: "0",
                          fontSize: "2.5vh",
                          color: "#000000",
                        }}
                      >
                        Rating: {movie.rating}
                      </p>
                    </div>
                  </div>
                ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Genre;
