import React, { useEffect } from "react";
import { Link, NavLink } from "react-router-dom";
import pic from "../images/homepage.jpg";

function Contact() {
  useEffect(() => {
    return () => {};
  }, []);

  return (
    <div style={{ padding: "20px" }}>
      <div
        style={{
          maxHeight: "800px",
          overflowY: "scroll",
          border: "1px solid #ccc",
          padding: "10px",
          marginTop: "20px",
        }}
      >
        {/* 서비스 이용 약관 텍스트 */}
        <h1>Contact Sales</h1>
        <p>
          If you want to send us a business mail, send us an e-mail:
          sales@inbang.com
        </p>
        <h1>Report Error</h1>
        <p>
          If you want to report an error, or if you want to make a suggestion,
          send us an e-mail: help@inbang.com
        </p>

        {/* 약관의 나머지 내용 */}
      </div>

      {/* Back Button */}
      <button
        onClick={handleGoBack}
        style={{
          position: "fixed",
          top: "2vh",
          right: "2vw",
          cursor: "pointer",
          border: "0.4vh solid rgba(18, 0, 184, 1)",
          backgroundColor: "rgba(18, 0, 134, 1)",
          borderRadius: "1vh",
          color: "#ffffff",
          padding: "0.01vh 1vw",
          fontSize: "4.5vh",
          fontWeight: "bolder",
          transform: "scaleY(-1)",
          zIndex: 3,
        }}
      >
        ↩
      </button>

      {/* About Us Button */}
      <div
        style={{
          position: "fixed",
          bottom: "3vh",
          right: "86vh",
          zIndex: 2,
        }}
      >
        <Link to="/about-us">
          <button
            style={{
              cursor: "pointer",
              backgroundColor: "transparent",
              border: "none",
              color: "#0008ff",
              fontSize: "3vh",
              marginTop: "2vh",
              fontWeight: "bold",
            }}
          >
            About Us
          </button>
        </Link>
      </div>

      {/* Contact Button */}
      <div
        style={{
          position: "fixed",
          bottom: "3vh",
          right: "66vh",
          zIndex: 2,
        }}
      >
        <Link to="/contact">
          <button
            style={{
              cursor: "pointer",
              backgroundColor: "transparent",
              border: "none",
              color: "#0008ff",
              fontSize: "3vh",
              marginTop: "2vh",
              fontWeight: "bold",
            }}
          >
            Contact
          </button>
        </Link>
      </div>

      {/* Terms of Service Button */}
      <div
        style={{
          position: "fixed",
          bottom: "3vh",
          right: "33vh",
          zIndex: 2,
        }}
      >
        <Link to="/terms-of-service">
          <button
            style={{
              cursor: "pointer",
              backgroundColor: "transparent",
              border: "none",
              color: "#0008ff",
              fontSize: "3vh",
              marginTop: "2vh",
              fontWeight: "bold",
            }}
          >
            Terms of Service
          </button>
        </Link>
      </div>

      {/* Privacy Policy Button */}
      <div
        style={{
          position: "fixed",
          bottom: "3vh",
          right: "3vh",
          zIndex: 2,
        }}
      >
        <Link to="/privacy-policy">
          <button
            style={{
              cursor: "pointer",
              backgroundColor: "transparent",
              border: "none",
              color: "#0008ff",
              fontSize: "3vh",
              marginTop: "2vh",
              fontWeight: "bold",
            }}
          >
            Privacy Policy
          </button>
        </Link>
      </div>
    </div>
  );
}

export default Contact;
