import React from "react";
import "../styles.css";
import { Link } from "react-router-dom";

function Login() {
  return (
    <div style={{ margin: "24px", position: "relative" }}>
      <h1
        style={{ color: "#004cff", fontSize: "100px", margin: 0, padding: 0 }}
      >
        인뱅
      </h1>
      <h2 style={{ color: "#004cff" }}>In-Bang</h2>

      <div
        style={{
          backgroundColor: "#1200b8",
          padding: "20px",
          color: "white",
          margin: "auto",
          position: "relative",
          width: "200px",
        }}
      >
        <Link
          to="/"
          style={{
            position: "absolute",
            right: "10px",
            top: "10px",
            textDecoration: "none",
          }}
        >
          <button
            style={{
              backgroundColor: "#fc2003",
              color: "#000000",
              padding: "5px",
            }}
          >
            X
          </button>
        </Link>
        <p
          style={{
            color: "#000000",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          Welcome!
        </p>
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            marginBottom: "10px",
          }}
        >
          <label>Username:</label>
          <input type="text" name="username" style={{ width: "200px" }} />
        </div>
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            marginBottom: "10px",
          }}
        >
          <label>Password:</label>
          <input type="password" name="password" style={{ width: "200px" }} />
        </div>
        <br />
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            marginTop: "20px",
          }}
        >
          <Link to="/login">
            <button
              style={{
                backgroundColor: "#000000",
                color: "#ffffff",
                padding: "5px",
              }}
            >
              Login
            </button>
          </Link>
        </div>
        <p
          style={{
            color: "#000000",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            whiteSpace: "nowrap",
          }}
        >
          Don't have an account? Click below.
        </p>
        <Link
          to="/registration"
          style={{
            color: "#ffffff",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          Register
        </Link>
      </div>
    </div>
  );
}

export default Login;
