import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { logout } from "../actions/userActions";
import { Navbar, Container, Nav, NavDropdown } from "react-bootstrap";
import { LinkContainer } from "react-router-bootstrap";
import pic from "../images/dashicon.png";
import pic1 from "../images/searchicon.png";
import userIcon from "../images/usericon.png";

function Header() {
  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [showButtons, setShowButtons] = useState(false); // State to control button visibility

  const logoutHandler = () => {
    dispatch(logout());
    navigate("/");
  };

  const handleChangePassword = () => {
    navigate("/change-password");
  };

  const toggleButtons = () => {
    setShowButtons(!showButtons); // Toggle button visibility
  };

  const headerStyle = {
    alignItems: "center",
    position: "relative",
    backgroundColor: "#1f1f1f",
    padding: "20px",
    display: "flex",
    justifyContent: "space-between",
    flexDirection: "column",
    textAlign: "center",
  };

  const containerStyle = {
    display: "flex",
    alignItems: "center",
    justifyContent: "flex-start",
    width: "100%",
    marginBottom: "10px",
  };

  const imgStyle = {
    width: "200px",
    height: "200px",
    marginTop: "-50px",
  };

  const searchBarStyle = {
    flex: "1",
    display: "flex",
    justifyContent: "center",
    backgroundColor: "#fff",
    borderRadius: "5px",
    padding: "5px",
    border: "1px solid #ccc",
    marginTop: "-150px",
    borderRadius: "30px",
  };

  const moviesStyle = {
    margin: "0",
  };

  const linkContainerStyle = {
    display: "flex",
    justifyContent: "flex-start",
    width: "100%",
    paddingLeft: "0px", // Move them on the left!
  };

  const linkStyle = {
    marginTop: "-70px",
    marginLeft: "10px",
    marginRight: "70px",
    cursor: "pointer",
    fontSize: "20px",
    backgroundColor: "transparent",
    border: "transparent",
    color: "#ff001e",
  };

  const searchStyle = {
    width: "50px",
    height: "50px",
    cursor: "pointer",
  };

  const profileContainerStyle = {
    position: "absolute",
    top: "20px",
    right: "30px",
  };

  return (
    <header style={headerStyle}>
      <div style={containerStyle}>
        <img src={pic} alt="dashicon" style={imgStyle} />
      </div>
      <div style={searchBarStyle}>
        <img src={pic1} alt="searchicon" style={searchStyle} />
        <input
          style={{
            padding: "5px",
            backgroundColor: "transparent",
            borderRadius: "0px",
            border: "none",
            outline: "none",
          }}
          type="text"
          placeholder="Type something to search"
        />
      </div>
      <div>
        <br />
        <br />
        <br />
        <br />
      </div>
      <div style={linkContainerStyle}>
        <Link to="/movies">
          <button style={linkStyle}>Movies</button>
        </Link>
        <Link to="/tv-shows">
          <button style={linkStyle}>TV Shows</button>
        </Link>
        <Link to="/genre">
          <button style={linkStyle}>Genre</button>
        </Link>
      </div>
      {userInfo ? (
        <div style={profileContainerStyle}>
          <div>
            <img
              src={userIcon}
              alt="user icon"
              style={{
                cursor: "pointer",
                width: "40px",
                height: "40px",
                padding: "2px",
                border: "2px solid #ffffff",
                borderRadius: "50%",
              }}
              onClick={toggleButtons}
            />
            {/* User's name */}
            <p style={{ color: "#ffffff" }}>{userInfo.name}</p>
          </div>
          {showButtons && (
            <div style={{ position: "absolute" }}>
              <br />
              <br />
              <button onClick={handleChangePassword}>Change Password</button>
              <br /> {/* Line break */}
              <button onClick={logoutHandler}>Logout</button>
            </div>
          )}
        </div>
      ) : null}
    </header>
  );
}

export default Header;
