import React, { useEffect, useState } from "react";
import Header from "../components/Header";
import { useNavigate } from "react-router-dom";
import { logout } from "../actions/userActions";
import { useDispatch, useSelector } from "react-redux";
import sampleVideo from "../videos/sampleVideo.mp4";

const Movie = () => {
  const [selectedMovie, setSelectedMovie] = useState(null);
  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [showMessage, setShowMessage] = useState(false); // State to control message visibility

  useEffect(() => {
    // Show message for a short duration before redirecting
    if (!userInfo) {
      setShowMessage(true); // Show the message
      const timer = setTimeout(() => {
        navigate("/login"); // Redirect to login after a delay
      }, 2000); // Delay in milliseconds (e.g., 2000 milliseconds = 2 seconds)
      return () => clearTimeout(timer); // Clear the timer if the component unmounts
    }
  }, [userInfo, navigate]);

  const logoutHandler = () => {
    dispatch(logout());
    localStorage.removeItem("user");
    navigate("/login"); // Redirect to login after logout
  };

  const errormsg = {
    color: "#ff0000",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "100vh", // Set height to full viewport height
    fontSize: "8vh",
    fontWeight: "bolder",
  };

  const movieData = [
    {
      title: "Talong 1",
      genre: "Action",
      rating: 8.5,
      description: "Description for Talong 1 movie.",
    },
    {
      title: "Talong 2",
      genre: "Comedy",
      rating: 7.8,
      description: "Description for Talong 2 movie.",
    },
    // Add descriptions for other movies
  ];

  const handlePosterClick = (index) => {
    setSelectedMovie(movieData[index]);
  };

  const handleCloseModal = () => {
    setSelectedMovie(null);
  };

  return (
    <div>
      <Header user={userInfo} logoutHandler={logoutHandler} />
      <div style={{ display: "flex", flexWrap: "wrap" }}>
        {movieData.map((movie, index) => (
          <div
            key={index}
            onClick={() => handlePosterClick(index)}
            style={{
              width: "25vw",
              margin: "2.5vw",
              backgroundColor: "#f9f9f9",
              borderRadius: "1vh",
              boxShadow: "0 0 1vw rgba(0, 0, 0, 0.1)",
              overflow: "hidden",
              cursor: "pointer",
            }}
          >
            {selectedMovie === movie && (
              <video
                controls
                src={sampleVideo}
                style={{
                  width: "100%",
                  height: "auto",
                  borderBottom: "0.25vh solid #ddd",
                }}
              />
            )}
            {selectedMovie !== movie && (
              <img
                src={require("../images/sample.png")}
                alt={`Poster ${index + 1}`}
                style={{
                  width: "100%",
                  height: "auto",
                  borderBottom: "0.25vh solid #ddd",
                }}
              />
            )}
            <div style={{ padding: "1.5vh" }}>
              <h2 style={{ margin: "0", fontSize: "2.5vh" }}>{movie.title}</h2>
              <p style={{ margin: "1vh 0", fontSize: "2vh" }}>
                Genre: {movie.genre}
              </p>
              <p style={{ margin: "1vh 0", fontSize: "2vh" }}>
                Rating: {movie.rating}
              </p>
              <p style={{ margin: "1vh 0", fontSize: "2vh" }}>
                {movie.description}
              </p>
            </div>
          </div>
        ))}
      </div>
      {selectedMovie && (
        <div
          style={{
            position: "fixed",
            top: "0",
            left: "0",
            width: "100%",
            height: "100%",
            backgroundColor: "rgba(0, 0, 0, 0.5)",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            zIndex: "999",
          }}
          onClick={handleCloseModal}
        >
          <video
            controls
            autoPlay
            src={sampleVideo}
            style={{
              maxWidth: "80vw",
              maxHeight: "80vh",
            }}
          />
        </div>
      )}
    </div>
  );
};

export default Movie;
