import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { logout } from "../actions/userActions";
import pic from "../images/usersettings.jpg";
import pic1 from "../images/usericon.png";

function UserSettings() {
  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [showButtons, setShowButtons] = useState(false); // State to control button visibility
  const [showMessage, setShowMessage] = useState(false); // State to control message visibility

  useEffect(() => {
    // Show message for a short duration before redirecting
    if (!userInfo) {
      setShowMessage(true); // Show the message
      const timer = setTimeout(() => {
        navigate("/login"); // Redirect to login after a delay
      }, 2000); // Delay in milliseconds (e.g., 2000 milliseconds = 2 seconds)
      return () => clearTimeout(timer); // Clear the timer if the component unmounts
    }
  }, [userInfo, navigate]);

  const logoutHandler = () => {
    dispatch(logout());
    navigate("/");
  };

  const handleChangePassword = () => {
    navigate("/change-password");
  };

  const handleUserSettings = () => {
    navigate("/user-settings");
  };

  const toggleButtons = () => {
    setShowButtons(!showButtons); // Toggle button visibility
  };

  const errormsg = {
    color: "#ff0000",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "100vh", // Set height to full viewport height
    fontSize: "100px",
    fontWeight: "bolder",
  };

  // If user is not logged in, only show the "Login first!" message
  if (!userInfo) {
    return <div style={errormsg}>Login first!</div>;
  }

  return (
    <div
      style={{
        position: "absolute",
        width: "100vw",
        height: "100vh",
        overflow: "hidden",
      }}
    >
      {/* Black overlay for bg
      <div
        style={{
          position: "absolute",
          width: "100%",
          height: "100%",
          backgroundColor: "rgba(0, 0, 0, 0.5)"
          zIndex: 0,
        }}
      /> */}
      {/* User Information box */}
      <div
        style={{
          position: "fixed",
          top: "4vh",
          left: "25vh",
          width: "100vw",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          textAlign: "center",
          padding: "2vh",
          boxSizing: "border-box",
          zIndex: 1, // Ensure it's above the overlay
        }}
      >
        {/* Edit width of box here */}
        <p
          style={{
            color: "#ffffff",
            width: "70vw",
            backgroundColor: "rgba(0, 0, 38, 0.7)",
            borderRadius: "1vh",
            padding: "2vh",
            textAlign: "center",
            fontSize: "3vh",
            fontWeight: "bolder",
          }}
        >
          User Information
        </p>
      </div>
      {/* Box */}
      <div
        style={{
          position: "fixed",
          top: "14vh",
          left: "25vh",
          width: "100vw",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          textAlign: "center",
          padding: "2vh",
          boxSizing: "border-box",
          zIndex: 1, // Ensure it's above the overlay
        }}
      >
        {/* Edit width of box here */}
        <p
          style={{
            color: "#ffffff",
            width: "70vw",
            backgroundColor: "rgba(0, 0, 38, 0.7)",
            borderRadius: "1vh",
            padding: "2vh",
            textAlign: "center",
            fontSize: "3vh",
          }}
        >
          <br />
          <br />
          <br />
          <br />
          <br />
          <br />
          <br />
          <br />
          <br />
          <br />
          <br />
          <br />
          <br />
          <br />
          <br />
          <br />
          <br />
          <br />
        </p>
      </div>
      {/* Profile Pic */}
      <div
        style={{
          position: "fixed",
          top: "29vh",
          left: "3vh",
          backgroundColor: "rgba(0, 0, 38, 0.7)",
          color: "#ffffff",
          borderRadius: "1vh",
          padding: "2vh 4vw",
          textAlign: "center",
          width: "15vw",
        }}
      >
        <img
          src={pic1}
          alt="user icon"
          style={{
            cursor: "pointer",
            width: "5vw",
            padding: "0.01vh",
            border: "0.5vh solid #555555",
            borderRadius: "50%",
          }}
        />
        <div style={{ color: "#ffffff", fontSize: "3vh" }}>
          {userInfo ? ( // Check if userInfo is not null
            <p>
              {userInfo.username}
              <br />
              {userInfo.email}
            </p>
          ) : null}
        </div>
      </div>
      {/* User Settings box */}
      <div
        style={{
          position: "fixed",
          top: "59vh",
          left: "1vh",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          textAlign: "center",
          padding: "2vh",
          boxSizing: "border-box",
          zIndex: 1, // Ensure it's above the overlay
        }}
      >
        {/* Edit width of box here */}
        <button
          style={{
            color: "#ffffff",
            width: "23vw",
            backgroundColor: "rgba(0, 0, 38, 0.7)",
            border: "transparent",
            borderRadius: "1vh",
            textAlign: "center",
            fontSize: "3vh",
            fontWeight: "bolder",
            cursor: "pointer",
            padding: "3.5vh",
          }}
        >
          Settings
        </button>
      </div>
      {/* User Plan box */}
      <div
        style={{
          position: "fixed",
          top: "70.5vh",
          left: "1vh",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          textAlign: "center",
          padding: "2vh",
          boxSizing: "border-box",
          zIndex: 1, // Ensure it's above the overlay
        }}
      >
        {/* Edit width of box here */}
        <button
          style={{
            color: "#ffffff",
            width: "23vw",
            backgroundColor: "rgba(0, 0, 38, 0.7)",
            border: "transparent",
            borderRadius: "1vh",
            textAlign: "center",
            fontSize: "3vh",
            fontWeight: "bolder",
            cursor: "pointer",
            padding: "3.5vh",
          }}
        >
          My Plans and Bills
        </button>
      </div>
      {/* User Logout box */}
      <div
        style={{
          position: "fixed",
          top: "82.5vh",
          left: "1vh",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          textAlign: "center",
          padding: "2vh",
          boxSizing: "border-box",
          zIndex: 1, // Ensure it's above the overlay
        }}
      >
        {/* Edit width of box here */}
        <button
          onClick={logoutHandler}
          style={{
            color: "#ffffff",
            width: "23vw",
            backgroundColor: "rgba(0, 0, 38, 0.7)",
            border: "transparent",
            borderRadius: "1vh",
            textAlign: "center",
            fontSize: "3vh",
            fontWeight: "bolder",
            cursor: "pointer",
            padding: "3.5vh",
          }}
        >
          Logout
        </button>
      </div>
    </div>
  );
}
export default UserSettings;
