import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { logout } from "../actions/userActions";
import { Navbar, Container, Nav, NavDropdown } from "react-bootstrap";
import { LinkContainer } from "react-router-bootstrap";
import pic from "../images/dashicon.png";
import pic1 from "../images/searchicon.png";
import userIcon from "../images/usericon.png";

function Header() {
  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [showButtons, setShowButtons] = useState(false); // State to control button visibility

  const logoutHandler = () => {
    dispatch(logout());
    navigate("/");
  };

  const handleUserSettings = () => {
    navigate("/user-settings");
  };

  const toggleButtons = () => {
    setShowButtons(!showButtons); // Toggle button visibility
  };

  const headerStyle = {
    alignItems: "center",
    position: "fixed", // Set position to fixed
    top: "0", // Position at the top of the viewport
    width: "100%", // Take up the full width
    backgroundColor: "#1f1f1f",
    padding: "2vh",
    display: "flex",
    justifyContent: "space-between",
    flexDirection: "column",
    textAlign: "center",
    zIndex: "1000", // Ensure it's above other elements
  };

  const containerStyle = {
    display: "flex",
    alignItems: "center",
    justifyContent: "flex-start",
    width: "100%",
    marginBottom: "1vh",
  };

  const imgStyle = {
    height: "35vh",
    marginTop: "-7vh",
  };

  const searchBarStyle = {
    justifyContent: "center",
    backgroundColor: "#ffffff",
    padding: "1vh",
    border: "0.1vh solid #ffffff",
    marginTop: "-27vh",
    borderRadius: "5vh",
  };

  const moviesStyle = {
    margin: "0",
  };

  const linkContainerStyle = {
    display: "flex",
    justifyContent: "flex-start",
    width: "100%",
    paddingLeft: "0px", // Move them on the left!
  };

  const linkStyle = {
    marginTop: "-70px",
    marginLeft: "10px",
    marginRight: "70px",
    cursor: "pointer",
    fontSize: "20px",
    backgroundColor: "transparent",
    border: "transparent",
    color: "#ff001e",
  };

  const searchStyle = {
    width: "50px",
    height: "50px",
    cursor: "pointer",
  };

  const profileContainerStyle = {
    position: "absolute",
    top: "20px",
    right: "20px",
  };

  const usernameStyle = {
    color: "#ffffff",
    marginRight: "10px", // Adjust margin as needed
  };

  const buttonStyle = {
    width: "80px", // Fixed width for buttons
    marginBottom: "5px", // Adjust margin as needed
    padding: "5px",
    cursor: "pointer",
    backgroundColor: "#000000",
    color: "#ffffff",
    borderRadius: "5px",
    border: "2px solid #000000",
  };

  return (
    <header style={headerStyle}>
      <div style={containerStyle}>
        <img src={pic} alt="dashicon" style={imgStyle} />
      </div>
      <div style={searchBarStyle}>
        <img src={pic1} alt="searchicon" style={searchStyle} />
        <input
          style={{
            padding: "5px",
            backgroundColor: "transparent",
            borderRadius: "0px",
            border: "none",
            outline: "none",
          }}
          type="text"
          placeholder="Type something to search"
        />
      </div>
      <div>
        <br />
        <br />
        <br />
        <br />
      </div>
      <div style={linkContainerStyle}>
        <Link to="/movies">
          <button style={linkStyle}>Movies</button>
        </Link>
        <Link to="/tv-shows">
          <button style={linkStyle}>TV Shows</button>
        </Link>
        <Link to="/genre">
          <button style={linkStyle}>Genre</button>
        </Link>
      </div>
      {userInfo ? (
        <div style={profileContainerStyle}>
          <div style={{ display: "flex", alignItems: "center" }}>
            <p style={usernameStyle}>{userInfo.username}</p>
            <img
              src={userIcon}
              alt="user icon"
              style={{
                cursor: "pointer",
                width: "3.2vw",
                padding: "0.01vh",
                border: "0.35vh solid #555555",
                borderRadius: "50%",
              }}
              onClick={toggleButtons}
            />
          </div>
          {showButtons && (
            <div style={{ position: "absolute", top: "60px", right: "-15px" }}>
              <button style={buttonStyle} onClick={handleUserSettings}>
                Settings
              </button>
              <br /> {/* Line break */}
              <button style={buttonStyle} onClick={logoutHandler}>
                Logout
              </button>
            </div>
          )}
        </div>
      ) : null}
    </header>
  );
}

export default Header;
