import React, { useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import pic from "../images/homepage.jpg";

function Contact() {
  useEffect(() => {
    return () => {};
  }, []);

  const navigate = useNavigate();

  const handleGoBack = () => {
    navigate(-1); // Go back one step in the browser history
  };

  return (
    <div style={{ padding: "2vw" }}>
      <div
        style={{
          maxHeight: "80vh",
          overflowY: "auto",
          border: "1vw solid #ccc",
          padding: "1vw",
          marginTop: "4vw",
        }}
      >
        {/* 서비스 이용 약관 텍스트 */}
        <h1>Contact Sales</h1>
        <p>
          If you want to send us a business mail, send us an e-mail:
          sales@inbang.com
        </p>
        <h1>Report Error</h1>
        <p>
          If you want to report an error, or if you want to make a suggestion,
          send us an e-mail: help@inbang.com
        </p>

        {/* 약관의 나머지 내용 */}
      </div>

      {/* Back Button */}
      <button
        onClick={handleGoBack}
        style={{
          position: "fixed",
          top: "2vh",
          right: "2vw",
          cursor: "pointer",
          border: "0.4vw solid rgba(18, 0, 184, 1)",
          backgroundColor: "rgba(18, 0, 134, 1)",
          borderRadius: "1vh",
          color: "#ffffff",
          padding: "0.01vh 1vw",
          fontSize: "4.5vh",
          fontWeight: "bolder",
          transform: "scaleY(-1)",
          zIndex: 3,
        }}
      >
        ↩
      </button>

      {/* Navigation Buttons */}
      <div
        style={{ position: "fixed", bottom: "3vh", right: "2vh", zIndex: 2 }}
      >
        <LinkButton to="/about-us">About Us</LinkButton>
        <LinkButton to="/contact">Contact</LinkButton>
        <LinkButton to="/terms-of-service">Terms of Service</LinkButton>
        <LinkButton to="/privacy-policy">Privacy Policy</LinkButton>
      </div>
    </div>
  );
}

const LinkButton = ({ to, children }) => (
  <Link to={to} style={{ textDecoration: "none" }}>
    <button
      style={{
        cursor: "pointer",
        backgroundColor: "transparent",
        border: "none",
        color: "#0008ff",
        fontSize: "3vh",
        marginTop: "2vh",
        fontWeight: "bold",
        marginRight: "1vh",
      }}
    >
      {children}
    </button>
  </Link>
);

export default Contact;
