import React, { useEffect, useState } from "react";
import Header from "../components/Header";
import { useNavigate } from "react-router-dom";
import { logout } from "../actions/userActions";
import { useDispatch, useSelector } from "react-redux";
import FooterTransparent from "../components/FooterTransparent";
import sampleVideo from "../videos/sampleVideo.mp4";
import "./mediaqueries.css";
import "./InvisiScroll.css";
import SearchBar from "../components/SearchBar";

const Dashboard = () => {
  const [selectedMovie, setSelectedMovie] = useState(null);
  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [showMessage, setShowMessage] = useState(false); // State to control message visibility
  const [searchQuery, setSearchQuery] = useState(""); // State for search query

  const movieData = [
    {
      title: "Talong 1",
      genre: "Action",
      rating: 8.5,
    },
    {
      title: "Talong 2",
      genre: "Comedy",
      rating: 7.8,
    },
    {
      title: "Talong 3",
      genre: "Drama",
      rating: 9.2,
    },
    {
      title: "Talong 4",
      genre: "Adventure",
      rating: 8.0,
    },
    {
      title: "Talong 5",
      genre: "Sci-Fi",
      rating: 8.7,
    },
    {
      title: "Talong 6",
      genre: "Thriller",
      rating: 7.9,
    },
  ];

  const SearchFunction = (query) => {
    setSearchQuery(query);
  };

  useEffect(() => {
    // Show message for a short duration before redirecting
    if (!userInfo) {
      setShowMessage(true);
      const timer = setTimeout(() => {
        navigate("/login");
      }, 2000);
      return () => clearTimeout(timer);
    }
  }, [userInfo, navigate, searchQuery]);

  const filteredMovies = movieData.filter((movie) =>
    movie.title.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const logoutHandler = () => {
    dispatch(logout());
    localStorage.removeItem("user");
    navigate("/login");
  };

  const errormsg = {
    color: "#ff0000",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "100vh",
    fontSize: "15vw",
    fontWeight: "bolder",
  };

  const handlePosterClick = (index) => {
    setSelectedMovie(movieData[index]);
  };

  const handleCloseModal = () => {
    setSelectedMovie(null);
  };

  if (!userInfo) {
    return <div style={errormsg}>Login first!</div>;
  }

  return (
    <div className="card-container container">
      <SearchBar onSearch={SearchFunction} />
      <FooterTransparent />
      <div>
        <Header user={userInfo} logoutHandler={logoutHandler} />
        <div
          style={{
            display: "flex",
            flexWrap: "nowrap",
            flexDirection: "row",
            alignItems: "center",
            justifyContent: "center",
            textAlign: "center",
            height: "25vh",
          }}
        >
          {filteredMovies.map((movie, index) => (
            <div
              key={index}
              onClick={() => handlePosterClick(index)}
              className="card-container"
              style={{
                display: "flex",
                flexDirection: "column",
                alignItems: "left",
                justifyContent: "center",
                textAlign: "center",
                left: "5%",
                marginLeft: "2vh",
                marginRight: "2vh",
                marginTop: "37vh",
                marginBottom: "-31vh",
                backgroundColor: "#f9f9f9",
                width: "17.5%",
                borderRadius: "1vh",
                boxShadow: "0 0 2.5vw rgba(0, 0, 0, 0.1)",
                overflow: "hidden",
                cursor: "pointer",
              }}
            >
              {selectedMovie === movie && (
                <video
                  controls
                  src={sampleVideo}
                  style={{
                    width: "100%",
                    height: "auto",
                    borderBottom: "0.25vh solid #ddd",
                  }}
                />
              )}
              {selectedMovie !== movie && (
                <img
                  src={require("../images/sample.png")}
                  alt={`Poster ${index + 1}`}
                  style={{
                    width: "100%",
                    height: "auto",
                    borderBottom: "0.25vh solid #ddd",
                  }}
                />
              )}
              {selectedMovie !== movie && (
                <div style={{ padding: "1.5vh", width: "100%" }}>
                  <h2
                    style={{ margin: "0", fontSize: "3vh", color: "#000000" }}
                  >
                    {movie.title}
                  </h2>
                  <p
                    style={{
                      margin: "1vh 0",
                      fontSize: "2.5vh",
                      color: "#000000",
                    }}
                  >
                    Genre: {movie.genre}
                  </p>
                  <p
                    style={{
                      margin: "1vh 0",
                      fontSize: "2.5vh",
                      color: "#000000",
                    }}
                  >
                    Rating: {movie.rating}
                  </p>
                </div>
              )}
            </div>
          ))}
          {selectedMovie && (
            <div
              style={{
                position: "fixed",
                top: 0,
                left: 0,
                width: "100%",
                height: "100%",
                backgroundColor: "rgba(0, 0, 0, 0.5)",
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                zIndex: "999",
              }}
              onClick={handleCloseModal}
            >
              <video
                controls
                autoPlay
                src={sampleVideo}
                style={{
                  maxWidth: "80vw",
                  maxHeight: "80vh",
                }}
              />
            </div>
          )}
        </div>
        <br />
      </div>
    </div>
  );
};

export default Dashboard;
