import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../styles.css";
import pic from "../images/wp.jpg";
import axios from "axios";

const Registration = () => {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    phoneNumber: "",
    email: "",
    username: "",
    password: "",
    error: "",
  });

  const { firstName, lastName, phoneNumber, email, username, password, error } =
    formData;

  const handleChange = (name) => (event) => {
    setFormData({ ...formData, [name]: event.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("/registration/", {
        first_name: firstName,
        last_name: lastName,
        phone_number: phoneNumber,
        email,
        username,
        password,
      });

      console.log(response.data); // Handle the response data as needed

      // Registration successful, redirect to subscription or home page
      window.location.href = "/subscription";
    } catch (error) {
      console.error(error.response.data.error); // Log the error response
      setFormData({ ...formData, error: error.response.data.error }); // Set the error message
    }
  };

  return (
    <div
      style={{
        position: "relative",
        height: "100vh",
        width: "100vw",
        overflow: "hidden",
      }}
    >
      {/* Your JSX content here */}
      <form onSubmit={handleSubmit}>
        {/* Form fields here */}
        {/* Display error message */}
        {error && <p style={{ color: "red", textAlign: "center" }}>{error}</p>}
        <button type="submit">Register</button>
      </form>
    </div>
  );
};

export default Registration;
